import React from "react";
import ReactDOM from "react-dom";
//import "./index.css";
//import App from "./App";
import "bootstrap/dist/css/bootstrap.min.css";
import reportWebVitals from "./reportWebVitals";
import { Container, Row, Col } from "react-bootstrap";
import styled from "styled-components";

export const StyledCol = styled(Col)`
  padding-left: 0.5 rem;
  padding-right: 0.5 rem;
  border-style: solid;
`;

export const StyledRow = styled(Row)`
  padding-top: 0.5 rem;
  margin-left: -0.5 rem;
  margin-right: ;
  &:last-child {
    padding-bottom: 1rem;
  }
`;

const calcRentReturn = (homePrice, monthlyRent, taxRate, monthlyHOA) => {
  const totalRate =
    ((parseFloat(monthlyRent) - parseFloat(monthlyHOA)) * 12) /
      parseFloat(homePrice) -
    parseFloat(taxRate);
  return totalRate;
};

const AnalyticalTool = () => {
  const [downPayment, setDownPayment] = React.useState(0.2);
  const [loanRate, setLoanRate] = React.useState(0.03);
  const [homePrice, setHomePrice] = React.useState(500000);
  const [monthlyRent, setMonthlyRent] = React.useState(3000);
  const [monthlyHOA, setMonthlyHOA] = React.useState(500);
  const [taxRate, setTaxRate] = React.useState(0.02);

  return (
    <Container>
      <StyledRow>
        <StyledCol>
          <h1>Rent or Loan</h1>
          <p>Assuming we bought a house for rent</p>
        </StyledCol>
      </StyledRow>
      <StyledRow>
        <StyledCol sm={4}>
          <form>
            <label>
              Home Price:
              <input
                type="Number"
                value={homePrice}
                onChange={(event) => {
                  setHomePrice(event.target.value);
                }}
              />
            </label>
            <label>
              Monthly Rent:
              <input
                type="Number"
                value={monthlyRent}
                onChange={(event) => {
                  setMonthlyRent(event.target.value);
                }}
              />
            </label>
            <label>
              Monthly HOA:
              <input
                type="Number"
                value={monthlyHOA}
                onChange={(event) => {
                  setMonthlyHOA(event.target.value);
                }}
              />
            </label>
            <label>
              Property Tax:
              <input
                type="Number"
                value={taxRate}
                onChange={(event) => {
                  setTaxRate(event.target.value);
                }}
              />
            </label>
            <label>
              Down Payment:
              <input
                type="Number"
                value={downPayment}
                onChange={(event) => {
                  setDownPayment(event.target.value);
                }}
              />
            </label>
            <label>
              Loan Rate:
              <input
                type="Number"
                value={loanRate}
                onChange={(event) => {
                  setLoanRate(event.target.value);
                }}
              />
            </label>
          </form>
        </StyledCol>
        <StyledCol sm={8}>
          <p>Rent Annual Return:</p>
          <p>{calcRentReturn(homePrice, monthlyRent, taxRate, monthlyHOA)}</p>
        </StyledCol>
      </StyledRow>
    </Container>
  );
};

ReactDOM.render(
  <React.Fragment>
    <AnalyticalTool />
  </React.Fragment>,
  document.getElementById("root")
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
